enum CNAS_XSD_SYS_ACQ_STA_ENUM
{
    /* SYSTEM ACQUIRE状态机，初始状态 */
    CNAS_XSD_SYS_ACQ_STA_INIT                               = 0x00,

    /* 等待CAS回复同步确认消息 */
    CNAS_XSD_SYS_ACQ_STA_WAIT_CAS_SYNC_CNF                  = 0x01,

    /* 等待CAS上报OHM指示 */
    CNAS_XSD_SYS_ACQ_STA_WAIT_CAS_OHM_IND                   = 0x02,

    /* 等待CAS回复停止同步确认消息 */
    CNAS_XSD_SYS_ACQ_STA_WAIT_CAS_STOP_SYNC_CNF             = 0x03,

    CNAS_XSD_SYS_ACQ_STA_BUTT
};
typedef VOS_UINT32 CNAS_XSD_SYS_ACQ_STA_ENUM_UINT32;


